sequence.script:

// Generate url with parameters
var relatedModule = mc.getProperty('uri.var.relatedModule');
var id = mc.getProperty('uri.var.id');
var urlQuery = mc.getProperty('uri.var.urlQuery');
var moduleType = mc.getProperty('uri.var.moduleType');
var xmlData = mc.getProperty('uri.var.xmlData');

//Append mandatory url parameters
urlQuery += 'id=' + id + '&';
urlQuery +=	'relatedModule=' + encodeURIComponent(relatedModule) + '&';
urlQuery +=	 'xmlData=' + encodeURIComponent(xmlData) + '&';					

urlQuery = '/' + encodeURIComponent(moduleType) + '/updateRelatedRecords?' + urlQuery.substring(0, urlQuery.length-1);
mc.setProperty('uri.var.urlQuery', urlQuery);
